import { useState } from "react";
import { TextField, Box, Button } from "@material-ui/core";

export default function CardMargens({takeResult,validacao}) {

    const [receitaBruta, setReceitaBruta] = useState("");
    const [receitaLiquida, setReceitaLiquida] = useState("");
    const [lucroBruto, setlucroBruto] = useState("");
    const [lucroOperacional, setlucroOperacional] = useState("");
    const [lucroLiquido, setlucroLiquido] = useState("");
    const [erroReceitaBruta, setErroReceitaBruta] = useState({value:false,message:""});
    const [erroReceitaLiquida, setErroReceitaLiquida] = useState({value:false,message:""});
    const [erroLucroBruto, setErroLucroBruto] = useState({value:false,message:""});
    const [erroLucroOperacional, setErroLucroOperacional] = useState({value:false,message:""});
    const [erroLucroLiquido, setErroLucroLiquido] = useState({value:false,message:""});


    function handleSendValues(){

        const data = {
            receitaBruta,
            receitaLiquida,
            lucroBruto,
            lucroOperacional,
            lucroLiquido
        }

        const optionsFetch = {
            method: "POST",
            headers: {
                'Accept': "application/json",
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
        }
        fetch("http://localhost:5050/calcular", optionsFetch)
            .then(resp => resp.json())
            .then(resp => takeResult(resp));

    }

    return (
        <Box sx={{ width: "300px", backgroundColor: "#f4f4f4", padding: "0px 20px", borderRadius: "10px", boxSizing: "border-box" }}>
            <Box sx={{ height: "500px", display: "flex", flexDirection: "column", justifyContent: "space-around" }}>
                <TextField
                    value={receitaBruta}
                    id="receitaBruta"
                    label="Digite a Receita Bruta"
                    error={erroReceitaBruta.value}
                    helperText={erroReceitaBruta.message}
                    onBlur={e =>{
                        const [valido,message]= validacao(e);
                        setErroReceitaBruta({
                            value:valido,
                            message:message
                        })
                    }}
                    onChange={e => {
                        setReceitaBruta(e.target.value)
                    }}
                />
                <TextField
                    value={receitaLiquida} 
                    id="receitaLiquida" 
                    label="Digite a Receita Líquida"
                    onChange={e => {
                        setReceitaLiquida(e.target.value)
                    }}
                />
                <TextField
                    value={lucroBruto} 
                    id="lucroBruto" 
                    label="Digite o Lucro Bruto"
                    onChange={e => {
                        setlucroBruto(e.target.value)
                    }}
                />
                <TextField
                    value={lucroOperacional} 
                    id="lucroOperacional" 
                    label="Digite a Lucro Operacional"
                    onChange={e => {
                        setlucroOperacional(e.target.value)
                    }}
                />
                <TextField
                    value={lucroLiquido}
                    id="lucroLiquido" 
                    label="Digite a Lucro Líquido"
                    onChange={e => {
                        setlucroLiquido(e.target.value)
                    }}
                />
                <Button 
                    sx={{ height: "56px" }} 
                    onClick={e => {
                        handleSendValues();
                        console.log("Enviado");
                    }}
                    color="success" 
                    variant="outlined">
                    Enviar
                </Button>
            </Box>
        </Box>

    )
}
