import { useState } from "react";
import { TextField, Box, Button } from "@material-ui/core";

export default function CardMargens() {

    const [receitaLiquida, setReceitaLiquida] = useState();
    const [receitaBruta, setReceitaBruta] = useState();
    const [lucroBruto, setlucroBruto] = useState();
    const [lucroOperacional, setlucroOperacional] = useState();
    const [lucroLiquido, setlucroLiquido] = useState();

    function handleSendValues(){

        const data = {
            receitaBruta,
            receitaLiquida,
            lucroBruto,
            lucroOperacional,
            lucroLiquido
        }

        const optionsFetch = {
            method: "POST",
            headers: {
                'Accept': "application/json",
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
        }
        fetch("http://localhost:5050/calcular", optionsFetch)
            .then(resp => resp.json())
            .then(resp => this.props.takeResult(resp));

    }

    return (
        <Box sx={{ width: "300px", backgroundColor: "#f4f4f4", padding: "0px 20px", borderRadius: "10px", boxSizing: "border-box" }}>
            <Box sx={{ height: "500px", display: "flex", flexDirection: "column", justifyContent: "space-around" }}>
                <TextField
                    type="number"
                    id="receitaBruta"
                    label="Digite a Receita Bruta"
                    onChange={e => {

                        if(e.target.value % 3 === 0){
                            e.target.value.concat('.');
                        }
                        setReceitaBruta(e.target.value)
                        console.log(receitaBruta);

                    }}
                />
                <TextField 
                    id="receitaLiquida" 
                    label="Digite a Receita Líquida"
                    onChange={e => {
                        setReceitaLiquida(e.target.value)
                    }}
                />
                <TextField 
                    id="lucroBruto" 
                    label="Digite o Lucro Bruto"
                    onChange={e => {
                        setlucroBruto(e.target.value)
                    }}
                />
                <TextField 
                    id="lucroOperacional" 
                    label="Digite a Lucro Operacional"
                    onChange={e => {
                        setlucroOperacional(e.target.value)
                    }}
                />
                <TextField 
                    id="lucroLiquido" 
                    label="Digite a Lucro Líquido"
                    onChange={e => {
                        setlucroLiquido(e.target.value)
                    }}
                />
                <Button 
                    sx={{ height: "56px" }} 
                    onClick={e => handleSendValues()} 
                    color="success" 
                    variant="outlined">
                    Enviar
                </Button>
            </Box>
        </Box>

    )
}
