import { Component } from "react";
import { Box } from "@material-ui/system";
import CardMargens from "../CardMargens";
import CardResult from "../CardResult";


export default class CardMain extends Component{

    constructor(props){
        super(props)

        this.result = null;

        this.state = {
            result:this.result
        }
    }


    takeResult(result){

        this.result = {
            margemBruta:result.margemBruta,
            margemOperacional:result.margemOperacional,
            margemLiquida:result.margemLiquida
        }

        this.setState({
            result:this.result
        })

    }

    render(){

        return(
            <Box sx={{height:"500px",width:"50%", display:"flex",alignItems:"center", justifyContent:"space-around"}}>
                <CardMargens takeResult={this.takeResult.bind(this)}/>
                <CardResult result={this.result}/>
            </Box>
        )
    }
}
