import { useState } from "react";
import { Box, Button } from "@material-ui/core";
import TextFieldEdited from "./TextFieldEdited";

export default function CardMargens({takeResult,validacao}) {

    const [receitaBruta, setReceitaBruta] = useState("");
    const [receitaLiquida, setReceitaLiquida] = useState("");
    const [lucroBruto, setlucroBruto] = useState("");
    const [lucroOperacional, setlucroOperacional] = useState("");
    const [lucroLiquido, setlucroLiquido] = useState("");


    function handleSendValues(){

        const data = {
            receitaBruta,
            receitaLiquida,
            lucroBruto,
            lucroOperacional,
            lucroLiquido
        }

        const optionsFetch = {
            method: "POST",
            headers: {
                'Accept': "application/json",
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
        }
        fetch("http://localhost:5050/calcular", optionsFetch)
            .then(resp => resp.json())
            .then(resp => takeResult(resp));

    }

    return (
        <Box sx={{ width: "300px", backgroundColor: "#f4f4f4", padding: "0px 20px", borderRadius: "10px", boxSizing: "border-box" }}>
            <Box sx={{ height: "500px", display: "flex", flexDirection: "column", justifyContent: "space-around" }}>
                <TextFieldEdited valueText={receitaBruta} id={"receitaBruta"} label={"Digite a Receita Bruta"} 
                validacao={validacao}
                onChange={e => {setReceitaBruta(e.target.value);}}
                />
                <TextFieldEdited valueText={receitaLiquida} id={"receitaLiquida"} label={"Digite a Receita Líquida"} 
                validacao={validacao}
                onChange={e => {setReceitaLiquida(e.target.value);}}
                />
                
                <TextFieldEdited valueText={lucroBruto} id={"lucroBruto"} label={"Digite o Lucro Bruto"} 
                validacao={validacao}
                onChange={e => {setlucroBruto(e.target.value);}}
                />
                
                <TextFieldEdited valueText={lucroOperacional} id={"lucroOperacional"} label={"Digite o Lucro Operacional"} 
                validacao={validacao}
                onChange={e => {setlucroOperacional(e.target.value);}}
                />
                
                <TextFieldEdited valueText={lucroLiquido} id={"lucroLiquido"} label={"Digite a Lucro Liquido"} 
                validacao={validacao}
                onChange={e => {setlucroLiquido(e.target.value);}}
                />
                
                
                <Button 
                    sx={{ height: "56px" }} 
                    onClick={e => {
                        handleSendValues();
                        console.log("Enviado");
                    }}
                    color="success" 
                    variant="outlined">
                    Enviar
                </Button>
            </Box>
        </Box>

    )
}
